import React,{useEffect, useMemo, useState} from 'react';


const OurTeam = () => {

    const [index, setIndex] = useState(0);
    const [data, setData] = useState([]);

    const url = 'https://dummyapi.io/data/api/user';


    const apiCall = async () => {
        try {
            const response = await fetch(url, {headers: {'app-id' : '60478cc549546035dd15897f'} });
            const users = await response.json();
            setData(users.data);
        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        apiCall();

    }, [])

    const {picture, firstName, lastName} = data[index];


    const leftArrowClick = (e) => {
        let leftArrow = setIndex(index - 1)
        return leftArrow;
    }

    const rightArrowClick = () => {
        let rightArrow = setIndex(index + 1)
        return rightArrow;
    }
        useMemo(() => {
            return(
                <div>
                    {console.log(data)}
                    <div className="container">
                <section className="section">
                    <div className="main-text">
                        <h1>Who We Are? Meet Our Team!</h1>
                        <p className="main-small-text">We listen, we discuss, we advise and develop. We love to learn </p>
                    </div>
                    <div className="team-contener">
                        <div className="team-member">
                            <div className="avatar">
                                <img src={picture} alt={firstName}/>
                            </div>
                            <div className="name">{firstName} {lastName}</div>
                            <div className="position">CEO</div>
                            <div className="text">Lorem ipsum dolor, 
                                sit amet consectetur adipisicing elit. Perspiciatis, 
                                molestiae!</div>
                            <div className="social-icons">
                                <ul className="social-ul">
                                    <li><ion-icon name="logo-facebook"></ion-icon></li>
                                    <li><ion-icon name="logo-twitter"></ion-icon></li>
                                    <li><ion-icon name="logo-pinterest"></ion-icon></li>
                                    <li><ion-icon name="logo-google"></ion-icon></li>
                                </ul>
                            </div>
                        </div>
                    </div>
                    <div className="arrows">
                        <button onClick={leftArrowClick} className="btn left-arrow"><ion-icon name="chevron-back-outline"></ion-icon></button>
                        <button onClick={rightArrowClick} className="btn right-arrow"><ion-icon name="chevron-forward-outline"></ion-icon></button>
                    </div>
                </section>
            </div>
                </div>
            );
        },[picture, firstName, lastName])
}


export default OurTeam;
